mapper.select:
SELECT * FROM points_history
WHERE user_id = #{userId}

	AND type = #{type}

ORDER BY date_issued DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM points_history
WHERE user_id = #{userId}

	AND type = #{type}

mapper.update:
UPDATE points_history
date_issued = #{dateIssued}, type = #{type}, amount = #{amount}, earning_source = #{earningSource}, spending_source = #{spendingSource}, user_id = #{userId}, order_code = #{orderCode}, comment = #{comment}
WHERE point_id = #{pointId}
