mapper.insert:
INSERT INTO QUIZ(
	QUIZNAME,
	QUIZCONTENT,
	QUIZANSWER
) VALUES (
	#{quizName},
	#{quizContent},
	#{quizAnswer}
)

mapper.select:
SELECT * FROM QUIZ
WHERE QUIZID NOT IN (
	SELECT QUIZID FROM QUIZHISTORY
	WHERE USER_ID=#{user_id}
)
ORDER BY RAND(#{random_seed}) LIMIT 5;

mapper.update:
UPDATE QUIZ SET
QUIZNAME = #{quizName}
QUIZCONTENT = #{quizContent}
QUIZANSWER = #{quizAnswer}
WHERE QUIZID = #{quizId}
